import Vue from "vue";
import Vuex from "vuex";
import Axios from "axios";

Vue.use(Vuex);

export const store = new Vuex.Store({
  state: {
    taskList: [{ id: 1, text: "test", status: 0, isActive: false }],
  },
  getters: {
    TASK: (state) => {
      return state.taskList;
    },
  },
  mutations: {
    SET_TASK: (state, payload) => {
      state.taskList = payload;
    },

    ADD_TASK: (state, payload) => {
      state.taskList.push(payload);
    },
    CHANGE_STATUS: (state, payload) => {

      let idx = store.taskList.findIndex((el) => el.id === payload);
      state.taskList[idx].isActive = !state.taskList[idx].isActive;
    },
  },
  actions: {
    GET_TASK: async (context) => {
      let { data } = await Axios.get("http://yourwebsite.com/api/todo");
      context.commit("SET_TASK", data);
    },
    SAVE_TASK: async (context, payload) => {
      await Axios.post("http://yourwebsite.com/api/todo");
      context.commit("ADD_TASK", payload);
    },
    changeStatus: async (context, payload) => {
      // TODO: найти таску по id
      // TODO: обновить статус на противоположный
      console.log("Store 1 ", payload);
      context.commit("CHANGE_STATUS", payload);
    },
    // deleteTask: function(payload) {
    //   // TODO: найти таску по id
    //   // TODO: удалить эту стаску из стора

    // }
  },
  modules: {},
});
